        #region 移除数组里相同的元素,返回字符串数组/去掉分隔符里相同的字符串，返回字符串
        /// <summary>
        /// 移除数组里相同的元素
        /// </summary>
        /// <param name="values"></param>
        /// <returns></returns>
        public static string[] RemoveSameString(string[] values)
        {
            List<string> list = new List<string>();
            for (int i = 0; i < values.Length; i++)//遍历数组成员
            {
                if (list.IndexOf(values[i].ToLower()) == -1)//对每个成员做一次新数组查询如果没有相等的则加到新数组
                    list.Add(values[i]);

            }
            return list.ToArray();
        }
        /// <summary>
        /// 以分隔符移除数组里相同的元素
        /// </summary>
        /// <param name="values"></param>
        /// <returns></returns>
        public static string RemoveSameString(string values, char splitChar)
        {
            string[] tempArr = values.Split(new char[] { splitChar }, StringSplitOptions.RemoveEmptyEntries);
            StringBuilder sb = new StringBuilder();
            foreach (string str in RemoveSameString(tempArr)) //调重载
            {
                sb.AppendFormat("{0}{1}", str, splitChar);
            }
            return sb.ToString();
        }
        #endregion
        #region 移除字符中最后一个字符分隔后的后面的所有字符
        public static string RemoveLastStringBySplitStr(string str,string splitStr) 
        {
            int lastIndex = 0; //变量声明
            lastIndex = str.LastIndexOf(splitStr);//获得.的位置
            return str.Substring(0, lastIndex);//获得目标字符串
        }
