        #region 获取数据的十六进制的文本表示/十六进制的文本转换成二进制
        public static string StringToHexString(string str, Encoding encode)
        {
            byte[] b = encode.GetBytes(str);//按照指定编码将string编程字节数组
            string result = string.Empty;
            for (int i = 0; i < b.Length; i++)//逐字节变为16进制字符，以%隔开
            {
                result += (Convert.ToString(b[i], 16) + "-");
            }
            return result.Remove(result.Length - 1);
        }
        //形如32-EF 16进制表示的字符串转换为字符串
        public static string HexStringToString(string hexStr, Encoding encode)
        {
            //以-分割字符串，并去掉空字符
            string[] chars = hexStr.Split(new char[] { '-' }, StringSplitOptions.RemoveEmptyEntries);
            byte[] b = new byte[chars.Length];
            //逐个字符变为16进制字节数据
            for (int i = 0; i < chars.Length; i++)
            {
                b[i] = Convert.ToByte(chars[i], 16);
            }
            //按照指定编码将字节数组变为字符串
            return encode.GetString(b);
        }

        /// <summary>
        /// 获取数据的十六进制的文本表示
        /// </summary>  
        public static string GetHexadecimalStr(byte[] data)
        {
            return GetHexadecimalStr(data, 0, data.Length);
        }
        /// <summary>
        /// 获取数据的十六进制的文本表示
        /// </summary>      
        public static string GetHexadecimalStr(byte[] data, int startIndex, int count)
        {
            string result;
            if (data == null)
                result = null;
            else if (data.Length == 0)
                result = "";
            else if (startIndex >= data.Length || startIndex + count > data.Length)
                result = null;
            else
            {
                StringBuilder stringBuilder = new StringBuilder();
                for (int i = startIndex; i < count; i++)
                {
                    stringBuilder.Append(data[i].ToString("X2"));
                    if (i < count - 1)
                    {
                        stringBuilder.Append("-");
                    }
                }
                result = stringBuilder.ToString();
            }
            return result;
        }
        /// <summary>
        /// GetHexadecimalStr方法的逆操作。将用于表示16进制值的字符串（如 47 BB 13 75 79 70 10）转换为真正的byte数组。
        /// </summary>
        /// <param name="str">表示16进制值的字符串（如 47 BB 13 75 79 70 10）</param>
        /// <param name="seperator">两个值之间的分隔符。比如“ ”。如果没有分隔符，传入null</param>        
        public static byte[] GetBytesFromHexadecimalStr(string str, string seperator)
        {
            byte[] result;
            if (str == null)
            {
                result = null;
            }
            else if (string.IsNullOrEmpty(str))
            {
                result = new byte[0];
            }
            else if (string.IsNullOrEmpty(seperator))
            {
                byte[] array = new byte[str.Length / 2];
                for (int i = 0; i < array.Length; i++)
                {
                    array[i] = byte.Parse(str.Substring(i * 2, 2), NumberStyles.AllowHexSpecifier);
                }
                result = array;
            }
            else
            {
                string[] array2 = str.Split(new string[]
				{
					seperator
				}, StringSplitOptions.RemoveEmptyEntries);
                byte[] array3 = new byte[array2.Length];
                for (int i = 0; i < array2.Length; i++)
                {
                    array3[i] = byte.Parse(array2[i], NumberStyles.AllowHexSpecifier);
                }
                result = array3;
            }
            return result;
        }
