        /// <summary>
        /// AnalyzeConfigString 各配置项采用分号分隔，使用"="连接key与value 调用时Dic的TryGetValue(TKey key, out TValue value);
        /// </summary>
        public static IDictionary<string, string> AnalyzeConfigString(string configStr)
        {
            IDictionary<string, string> result;
            if (configStr == null && configStr.Trim() == "")
            {
                result = null;
            }
            else
            {
                string[] array = configStr.Split(new char[]
				{
					';'
				});
                IDictionary<string, string> dictionary = new Dictionary<string, string>();
                string[] array2 = array;
                for (int i = 0; i < array2.Length; i++)
                {
                    string text = array2[i];
                    string text2 = text.Trim();
                    if (text2 != "")
                    {
                        string[] array3 = text2.Split(new char[]
						{
							'='
						});
                        dictionary.Add(array3[0].Trim(), array3[1].Trim());
                    }
                }
                result = dictionary;
            }
            return result;
        }
