        #region 字典里的键值合并成字符串
        public static string CombinDictionaryIntoString<TKey, TVal>(Dictionary<TKey, TVal> dic, char itemSeparator, char keyValSeparator)
        {
            string result;
            if (dic == null || dic.Count == 0)
            {
                result = "";
            }
            else
            {
                StringBuilder stringBuilder = new StringBuilder("");
                int num = 0;
                foreach (KeyValuePair<TKey, TVal> current in dic)
                {
                    stringBuilder.Append(string.Format("{0}{1}{2}", current.Key, keyValSeparator, current.Value));
                    if (num < dic.Count - 1)
                    {
                        stringBuilder.Append(itemSeparator.ToString());
                    }
                }
                result = stringBuilder.ToString();
            }
            return result;
        }
